import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useSearchParams } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import { ArrowLeft, Save } from 'lucide-react';
import './ContentEditor.css';

function ContentEditor() {
  const { id } = useParams();
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const isNew = id === 'new';
  const sectionId = searchParams.get('section_id');
  
  const [content, setContent] = useState({
    section_id: sectionId ? parseInt(sectionId) : '',
    screen_title: '',
    read_time_min: 5,
    content_markdown: '',
    order_index: 1
  });
  const [loading, setLoading] = useState(!isNew);
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    if (!user || !user.is_admin) {
      navigate('/admin');
      return;
    }
    if (!isNew) {
      loadContent();
    }
  }, [id, user, navigate, isNew]);

  const loadContent = async () => {
    try {
      const res = await axios.get(`/api/admin/learning-content/${id}`);
      setContent(res.data);
    } catch (error) {
      console.error('Error loading content:', error);
      alert('Failed to load content');
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async () => {
    if (!content.screen_title || !content.content_markdown) {
      alert('Screen title and content are required');
      return;
    }

    setSaving(true);
    try {
      if (isNew) {
        await axios.post('/api/admin/learning-content', content);
        navigate(`/admin/sections/${content.section_id}`);
      } else {
        await axios.put(`/api/admin/learning-content/${id}`, content);
      }
      alert('Content saved successfully!');
      navigate(`/admin/sections/${content.section_id}`);
    } catch (error) {
      alert(error.response?.data?.error || 'Failed to save content');
    } finally {
      setSaving(false);
    }
  };

  if (loading) return <div className="admin-loading">Loading...</div>;

  return (
    <div className="content-editor">
      <div className="editor-header">
        <button onClick={() => navigate(`/admin/sections/${content.section_id || sectionId}`)} className="btn-back">
          <ArrowLeft /> Back
        </button>
        <h1>{isNew ? 'Create Learning Content' : 'Edit Learning Content'}</h1>
      </div>

      <div className="editor-form">
        <div className="form-group">
          <label>Section ID</label>
          <input
            type="number"
            value={content.section_id}
            onChange={(e) => setContent({ ...content, section_id: parseInt(e.target.value) || '' })}
            disabled={!isNew}
          />
        </div>

        <div className="form-group">
          <label>Screen Title</label>
          <input
            type="text"
            value={content.screen_title}
            onChange={(e) => setContent({ ...content, screen_title: e.target.value })}
            placeholder="Title for this content screen"
          />
        </div>

        <div className="form-group">
          <label>Read Time (minutes)</label>
          <input
            type="number"
            value={content.read_time_min}
            onChange={(e) => setContent({ ...content, read_time_min: parseInt(e.target.value) || 5 })}
            min="1"
          />
        </div>

        <div className="form-group">
          <label>Content (Markdown)</label>
          <textarea
            value={content.content_markdown}
            onChange={(e) => setContent({ ...content, content_markdown: e.target.value })}
            rows="15"
            placeholder="Enter markdown content..."
            className="markdown-editor"
          />
        </div>

        <div className="form-group">
          <label>Order Index</label>
          <input
            type="number"
            value={content.order_index}
            onChange={(e) => setContent({ ...content, order_index: parseInt(e.target.value) || 1 })}
            min="1"
          />
        </div>

        <button onClick={handleSave} disabled={saving} className="btn-save">
          <Save /> {saving ? 'Saving...' : 'Save Content'}
        </button>
      </div>
    </div>
  );
}

export default ContentEditor;
