import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Trophy, Star, Target, Calendar, Award, Settings, LogOut } from 'lucide-react';
import axios from 'axios';
import './Profile.css';

const Profile = () => {
  const { user, logout } = useAuth();
  const [stats, setStats] = useState(null);
  const [achievements, setAchievements] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserStats = async () => {
      try {
        const [statsRes, achievementsRes] = await Promise.all([
          axios.get('/api/user/stats'),
          axios.get('/api/user/achievements')
        ]);
        
        setStats(statsRes.data);
        setAchievements(achievementsRes.data);
      } catch (err) {
        setError('Failed to load user statistics');
        console.error('Error fetching user stats:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchUserStats();
  }, []);

  const calculateLevel = (xp) => {
    return Math.floor(xp / 100) + 1;
  };

  const getLevelProgress = (xp) => {
    const currentLevel = calculateLevel(xp);
    const xpForCurrentLevel = (currentLevel - 1) * 100;
    const xpForNextLevel = currentLevel * 100;
    const progress = ((xp - xpForCurrentLevel) / (xpForNextLevel - xpForCurrentLevel)) * 100;
    return Math.min(progress, 100);
  };

  const getLevelTitle = (level) => {
    if (level >= 20) return 'Security Master';
    if (level >= 15) return 'Security Expert';
    if (level >= 10) return 'Security Specialist';
    if (level >= 5) return 'Security Enthusiast';
    return 'Security Beginner';
  };

  const handleLogout = () => {
    logout();
  };

  if (loading) {
    return (
      <div className="profile-container">
        <div className="loading">Loading profile...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="profile-container">
        <div className="error">{error}</div>
      </div>
    );
  }

  const userLevel = calculateLevel(user.xp);
  const levelProgress = getLevelProgress(user.xp);
  const levelTitle = getLevelTitle(userLevel);

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-info">
          <div className="avatar-section">
            <img
              src={`/avatars/${user.avatar}.svg`}
              alt={user.display_name}
              className="profile-avatar"
            />
            <div className="level-badge">
              Level {userLevel}
            </div>
          </div>
          <div className="user-details">
            <h1>{user.display_name}</h1>
            <p className="user-title">{levelTitle}</p>
            <p className="user-email">{user.email}</p>
          </div>
        </div>
        <div className="profile-actions">
          <button className="btn btn-secondary">
            <Settings size={16} />
            Settings
          </button>
          <button onClick={handleLogout} className="btn btn-danger">
            <LogOut size={16} />
            Logout
          </button>
        </div>
      </div>

      <div className="progress-section">
        <div className="level-progress">
          <div className="progress-header">
            <h3>Level Progress</h3>
            <span className="xp-display">{user.xp} XP</span>
          </div>
          <div className="progress-bar">
            <div 
              className="progress-fill" 
              style={{ width: `${levelProgress}%` }}
            ></div>
          </div>
          <div className="progress-labels">
            <span>Level {userLevel}</span>
            <span>Level {userLevel + 1}</span>
          </div>
        </div>
      </div>

      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">
            <Trophy size={24} />
          </div>
          <div className="stat-content">
            <div className="stat-value">{stats?.modulesCompleted || 0}</div>
            <div className="stat-label">Modules Completed</div>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">
            <Star size={24} />
          </div>
          <div className="stat-content">
            <div className="stat-value">{stats?.sectionsCompleted || 0}</div>
            <div className="stat-label">Sections Completed</div>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">
            <Target size={24} />
          </div>
          <div className="stat-content">
            <div className="stat-value">{stats?.quizzesPassed || 0}</div>
            <div className="stat-label">Quizzes Passed</div>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">
            <Calendar size={24} />
          </div>
          <div className="stat-content">
            <div className="stat-value">{stats?.daysActive || 0}</div>
            <div className="stat-label">Days Active</div>
          </div>
        </div>
      </div>

      <div className="achievements-section">
        <h2>Achievements</h2>
        <div className="achievements-grid">
          {achievements.length > 0 ? (
            achievements.map((achievement) => (
              <div
                key={achievement.id}
                className={`achievement-card ${achievement.earned ? 'earned' : 'locked'}`}
              >
                <div className="achievement-icon">
                  <Award size={24} />
                </div>
                <div className="achievement-content">
                  <h4>{achievement.title}</h4>
                  <p>{achievement.description}</p>
                  {achievement.earned && (
                    <span className="earned-date">
                      Earned {new Date(achievement.earnedAt).toLocaleDateString()}
                    </span>
                  )}
                </div>
              </div>
            ))
          ) : (
            <div className="empty-achievements">
              <Award size={48} />
              <h3>No achievements yet</h3>
              <p>Complete modules and quizzes to earn achievements!</p>
            </div>
          )}
        </div>
      </div>

      <div className="recent-activity">
        <h2>Recent Activity</h2>
        <div className="activity-list">
          {stats?.recentActivity?.length > 0 ? (
            stats.recentActivity.map((activity, index) => (
              <div key={index} className="activity-item">
                <div className="activity-icon">
                  {activity.type === 'module' && <Trophy size={16} />}
                  {activity.type === 'quiz' && <Star size={16} />}
                  {activity.type === 'section' && <Target size={16} />}
                </div>
                <div className="activity-content">
                  <div className="activity-title">{activity.title}</div>
                  <div className="activity-time">
                    {new Date(activity.timestamp).toLocaleDateString()}
                  </div>
                </div>
                <div className="activity-xp">+{activity.xp} XP</div>
              </div>
            ))
          ) : (
            <div className="empty-activity">
              <p>No recent activity</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
