import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { ArrowLeft, CheckCircle, XCircle, Trophy, Star } from 'lucide-react';
import axios from 'axios';
import './SectionQuiz.css';

const SectionQuiz = () => {
  const { sectionId } = useParams();
  const { user, updateUser } = useAuth();
  // const navigate = useNavigate(); // Unused for now
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [quizCompleted, setQuizCompleted] = useState(false);
  const [score, setScore] = useState(0);
  const [xpEarned, setXpEarned] = useState(0);
  const [showExplanation, setShowExplanation] = useState(false);

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await axios.get(`/api/sections/${sectionId}/questions`);
        setQuestions(response.data);
      } catch (err) {
        setError('Failed to load quiz questions');
        console.error('Error fetching questions:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchQuestions();
  }, [sectionId]);

  const handleAnswerSelect = (answer) => {
    setSelectedAnswer(answer);
  };

  const handleNext = async () => {
    if (selectedAnswer === null) return;

    const newAnswers = { ...answers, [currentQuestion]: selectedAnswer };
    setAnswers(newAnswers);

    // Check if answer is correct
    const currentQ = questions[currentQuestion];
    const isCorrect = selectedAnswer === currentQ.correct_answer;
    
    if (isCorrect) {
      setScore(score + 1);
    }

    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setSelectedAnswer(null);
      setShowExplanation(false);
    } else {
      // Quiz completed
      const finalScore = isCorrect ? score + 1 : score;
      const percentage = (finalScore / questions.length) * 100;
      const earnedXP = Math.round((percentage / 100) * 50); // Max 50 XP per quiz
      
      setScore(finalScore);
      setXpEarned(earnedXP);
      setQuizCompleted(true);

      // Submit quiz results
      try {
        const response = await axios.post(`/api/sections/${sectionId}/quiz`, {
          answers: newAnswers,
          score: finalScore,
          totalQuestions: questions.length
        });

        // Update user XP
        if (response.data.xpEarned) {
          updateUser({ ...user, total_xp: (user.total_xp || 0) + response.data.xpEarned });
        }
      } catch (err) {
        console.error('Error submitting quiz results:', err);
      }
    }
  };

  const handleRetry = () => {
    setCurrentQuestion(0);
    setSelectedAnswer(null);
    setAnswers({});
    setQuizCompleted(false);
    setScore(0);
    setXpEarned(0);
    setShowExplanation(false);
  };

  if (loading) {
    return (
      <div className="section-quiz-container">
        <div className="loading">Loading quiz...</div>
      </div>
    );
  }

  if (error || questions.length === 0) {
    return (
      <div className="section-quiz-container">
        <div className="error">{error || 'No questions available'}</div>
      </div>
    );
  }

  if (quizCompleted) {
    const percentage = (score / questions.length) * 100;
    const isPassed = percentage >= 70;

    return (
      <div className="section-quiz-container">
        <div className="quiz-results">
          <div className="results-header">
            <h1>Quiz Complete!</h1>
            <div className={`score-badge ${isPassed ? 'passed' : 'failed'}`}>
              {isPassed ? <CheckCircle size={24} /> : <XCircle size={24} />}
              {percentage}%
            </div>
          </div>

          <div className="results-stats">
            <div className="stat-card">
              <div className="stat-value">{score}</div>
              <div className="stat-label">Correct Answers</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{questions.length}</div>
              <div className="stat-label">Total Questions</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{xpEarned}</div>
              <div className="stat-label">XP Earned</div>
            </div>
          </div>

          {isPassed && (
            <div className="success-message">
              <Trophy size={32} />
              <h2>Congratulations!</h2>
              <p>You've successfully completed this section and earned {xpEarned} XP!</p>
            </div>
          )}

          {!isPassed && (
            <div className="failure-message">
              <XCircle size={32} />
              <h2>Keep Learning!</h2>
              <p>You need 70% to pass. Review the material and try again.</p>
            </div>
          )}

          <div className="results-actions">
            <button onClick={handleRetry} className="btn btn-secondary">
              Retry Quiz
            </button>
            <Link to="/modules" className="btn btn-primary">
              Back to Modules
            </Link>
          </div>
        </div>
      </div>
    );
  }

  const currentQ = questions[currentQuestion];
  const progress = ((currentQuestion + 1) / questions.length) * 100;

  return (
    <div className="section-quiz-container">
      <div className="quiz-header">
        <Link to={`/section/${sectionId}/learn`} className="back-button">
          <ArrowLeft size={20} />
          Back to Learning
        </Link>
        <div className="quiz-progress">
          <div className="progress-bar">
            <div className="progress-fill" style={{ width: `${progress}%` }}></div>
          </div>
          <span className="progress-text">
            Question {currentQuestion + 1} of {questions.length}
          </span>
        </div>
      </div>

      <div className="question-container">
        <div className="question-card">
          <div className="question-header">
            <h2>Question {currentQuestion + 1}</h2>
            <div className="question-points">
              <Star size={16} />
              1 point
            </div>
          </div>

          <div className="question-text">
            {currentQ.question_text}
          </div>

          <div className="answer-options">
            {currentQ.options.map((option, index) => (
              <button
                key={index}
                onClick={() => handleAnswerSelect(option)}
                className={`answer-option ${
                  selectedAnswer === option ? 'selected' : ''
                }`}
              >
                <div className="option-letter">
                  {String.fromCharCode(65 + index)}
                </div>
                <div className="option-text">{option}</div>
              </button>
            ))}
          </div>

          {showExplanation && (
            <div className="explanation">
              <h3>Explanation:</h3>
              <p>{currentQ.explanation}</p>
            </div>
          )}
        </div>

        <div className="quiz-navigation">
          <button
            onClick={handleNext}
            disabled={selectedAnswer === null}
            className="btn btn-primary"
          >
            {currentQuestion === questions.length - 1 ? 'Finish Quiz' : 'Next Question'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default SectionQuiz;
