import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import { ArrowLeft, Save, Plus, Edit, Trash2 } from 'lucide-react';
import './ModuleEditor.css';

function ModuleEditor() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const isNew = id === 'new';
  
  const [module, setModule] = useState({
    name: '',
    display_name: '',
    description: '',
    order_index: 1
  });
  const [sections, setSections] = useState([]);
  const [loading, setLoading] = useState(!isNew);
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    if (!user || !user.is_admin) {
      navigate('/admin');
      return;
    }
    if (!isNew) {
      loadModule();
    }
  }, [id, user, navigate, isNew]);

  const loadModule = async () => {
    try {
      const [moduleRes, sectionsRes] = await Promise.all([
        axios.get(`/api/admin/modules/${id}`),
        axios.get(`/api/admin/modules/${id}/sections`)
      ]);
      setModule(moduleRes.data);
      setSections(sectionsRes.data);
    } catch (error) {
      console.error('Error loading module:', error);
      alert('Failed to load module');
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      if (isNew) {
        await axios.post('/api/admin/modules', module);
        navigate('/admin');
      } else {
        await axios.put(`/api/admin/modules/${id}`, module);
      }
      alert('Module saved successfully!');
    } catch (error) {
      alert(error.response?.data?.error || 'Failed to save module');
    } finally {
      setSaving(false);
    }
  };

  const handleDeleteSection = async (sectionId) => {
    if (!window.confirm('Are you sure? This will also delete all questions and content in this section.')) {
      return;
    }
    try {
      await axios.delete(`/api/admin/sections/${sectionId}`);
      loadModule();
    } catch (error) {
      alert(error.response?.data?.error || 'Failed to delete section');
    }
  };

  if (loading) return <div className="admin-loading">Loading...</div>;

  return (
    <div className="module-editor">
      <div className="editor-header">
        <button onClick={() => navigate('/admin')} className="btn-back">
          <ArrowLeft /> Back
        </button>
        <h1>{isNew ? 'Create Module' : 'Edit Module'}</h1>
      </div>

      <div className="editor-form">
        <div className="form-group">
          <label>Name (internal)</label>
          <input
            type="text"
            value={module.name}
            onChange={(e) => setModule({ ...module, name: e.target.value })}
            placeholder="e.g., module-1-security"
          />
        </div>

        <div className="form-group">
          <label>Display Name</label>
          <input
            type="text"
            value={module.display_name}
            onChange={(e) => setModule({ ...module, display_name: e.target.value })}
            placeholder="e.g., Security Awareness Essentials"
          />
        </div>

        <div className="form-group">
          <label>Description</label>
          <textarea
            value={module.description || ''}
            onChange={(e) => setModule({ ...module, description: e.target.value })}
            rows="3"
            placeholder="Module description..."
          />
        </div>

        <div className="form-group">
          <label>Order Index</label>
          <input
            type="number"
            value={module.order_index}
            onChange={(e) => setModule({ ...module, order_index: parseInt(e.target.value) || 1 })}
          />
        </div>

        <button onClick={handleSave} disabled={saving} className="btn-save">
          <Save /> {saving ? 'Saving...' : 'Save Module'}
        </button>
      </div>

      {!isNew && (
        <div className="sections-section">
          <div className="sections-header">
            <h2>Sections</h2>
            <button onClick={() => navigate(`/admin/sections/new?module_id=${id}`)} className="btn-primary">
              <Plus /> Add Section
            </button>
          </div>
          <div className="sections-list">
            {sections.map(section => (
              <div key={section.id} className="section-item">
                <div className="section-info">
                  <h3>{section.display_name}</h3>
                  <p>{section.description || 'No description'}</p>
                  <div className="section-stats">
                    <span>{section.question_count || 0} questions</span>
                    <span>{section.content_count || 0} content items</span>
                  </div>
                </div>
                <div className="section-actions">
                  <button onClick={() => navigate(`/admin/sections/${section.id}`)} className="btn-edit">
                    <Edit /> Edit
                  </button>
                  <button onClick={() => handleDeleteSection(section.id)} className="btn-delete">
                    <Trash2 /> Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default ModuleEditor;
