import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { 
  Home, 
  BookOpen, 
  Trophy, 
  Shield, 
  User,
  LogOut
} from 'lucide-react';
import './Sidebar.css';

function Sidebar() {
  const { logout } = useAuth();
  const location = useLocation();

  const navItems = [
    { path: '/', icon: Home, label: 'Home', category: 'LEARNING' },
    { path: '/modules', icon: BookOpen, label: 'Learn', category: 'LEARNING' },
    { path: '/leaderboard', icon: Trophy, label: 'Leaderboards', category: 'LEARNING' },
    { path: '/profile', icon: Shield, label: 'Data Breaches', category: 'ACCOUNT' },
    { path: '/profile', icon: User, label: 'Profile', category: 'ACCOUNT' },
  ];

  const groupedItems = navItems.reduce((acc, item) => {
    if (!acc[item.category]) {
      acc[item.category] = [];
    }
    acc[item.category].push(item);
    return acc;
  }, {});

  const handleLogout = () => {
    logout();
  };

  return (
    <div className="sidebar">
      <div className="sidebar-content">
        {Object.entries(groupedItems).map(([category, items]) => (
          <div key={category} className="nav-section">
            <h3 className="nav-category">{category}</h3>
            <nav className="nav-list">
              {items.map((item) => {
                const Icon = item.icon;
                const isActive = location.pathname === item.path;
                
                return (
                  <Link
                    key={item.path}
                    to={item.path}
                    className={`nav-item ${isActive ? 'active' : ''}`}
                  >
                    <Icon className="nav-icon" />
                    <span className="nav-label">{item.label}</span>
                  </Link>
                );
              })}
            </nav>
          </div>
        ))}
        
        <div className="nav-section">
          <button onClick={handleLogout} className="nav-item logout-btn">
            <LogOut className="nav-icon" />
            <span className="nav-label">Logout</span>
          </button>
        </div>
      </div>
    </div>
  );
}

export default Sidebar;
