import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';

import { ArrowLeft, BookOpen, CheckCircle, Play } from 'lucide-react';
import axios from 'axios';
import './SectionLearn.css';

const SectionLearn = () => {
  const { sectionId } = useParams();

  const navigate = useNavigate();
  const [section, setSection] = useState(null);
  const [content, setContent] = useState([]);
  const [currentStep, setCurrentStep] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isCompleted, setIsCompleted] = useState(false);

  useEffect(() => {
    const fetchSectionData = async () => {
      try {
        const response = await axios.get(`/api/sections/${sectionId}`);
        setSection(response.data);
        
        // Generate learning content based on section
        const learningContent = generateLearningContent(response.data);
        setContent(learningContent);
      } catch (err) {
        setError('Failed to load section data');
        console.error('Error fetching section:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchSectionData();
  }, [sectionId]);

  const generateLearningContent = (sectionData) => {
    // This would typically come from a CMS or database
    // For now, we'll generate content based on the section title
    const baseContent = [
      {
        type: 'introduction',
        title: 'Introduction',
        content: `Welcome to ${sectionData.title}! In this section, you'll learn the fundamental concepts and practical applications of this important topic.`,
        duration: 2
      },
      {
        type: 'concept',
        title: 'Key Concepts',
        content: `Understanding the core principles is essential. Here are the main concepts you need to know:\n\n• First principle\n• Second principle\n• Third principle\n\nTake your time to absorb these concepts before moving forward.`,
        duration: 5
      },
      {
        type: 'example',
        title: 'Real-World Examples',
        content: `Let's look at some practical examples:\n\n**Example 1:** A common scenario where this applies\n**Example 2:** Another situation to consider\n**Example 3:** Advanced application\n\nThese examples will help you understand how to apply the concepts in real situations.`,
        duration: 4
      },
      {
        type: 'practice',
        title: 'Practice Exercise',
        content: `Now it's time to practice what you've learned:\n\n**Exercise:** Think about a situation where you might encounter this concept. How would you apply what you've learned?\n\nTake a moment to reflect on this before proceeding.`,
        duration: 3
      },
      {
        type: 'summary',
        title: 'Summary',
        content: `Great job! You've completed the learning content for ${sectionData.title}.\n\n**Key Takeaways:**\n• Main point 1\n• Main point 2\n• Main point 3\n\nYou're now ready to test your knowledge with the quiz!`,
        duration: 2
      }
    ];

    return baseContent;
  };

  const handleNext = async () => {
    if (currentStep < content.length - 1) {
      setCurrentStep(currentStep + 1);
    } else {
      // Mark section as learned
      try {
        await axios.post(`/api/sections/${sectionId}/learn`);
        setIsCompleted(true);
      } catch (err) {
        console.error('Error marking section as learned:', err);
      }
    }
  };

  const handlePrevious = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleComplete = async () => {
    // Mark section as learned if not already completed
    if (!isCompleted) {
      try {
        await axios.post(`/api/sections/${sectionId}/learn`);
        setIsCompleted(true);
      } catch (err) {
        console.error('Error marking section as learned:', err);
        return; // Don't navigate if marking as learned fails
      }
    }
    
    // Navigate to quiz
    navigate(`/section/${sectionId}/quiz`);
  };

  if (loading) {
    return (
      <div className="section-learn-container">
        <div className="loading">Loading learning content...</div>
      </div>
    );
  }

  if (error || !section) {
    return (
      <div className="section-learn-container">
        <div className="error">{error || 'Section not found'}</div>
      </div>
    );
  }

  const currentContent = content[currentStep];
  const progress = ((currentStep + 1) / content.length) * 100;

  return (
    <div className="section-learn-container">
      <div className="learn-header">
        <Link to={`/module/${section.module_id}`} className="back-button">
          <ArrowLeft size={20} />
          Back to Module
        </Link>
        <div className="section-info">
          <h1>{section.title}</h1>
          <p>{section.description}</p>
        </div>
      </div>

      <div className="progress-bar">
        <div className="progress-fill" style={{ width: `${progress}%` }}></div>
        <span className="progress-text">
          Step {currentStep + 1} of {content.length}
        </span>
      </div>

      <div className="content-container">
        <div className="content-card">
          <div className="content-header">
            <div className="content-icon">
              {currentContent.type === 'introduction' && <BookOpen size={24} />}
              {currentContent.type === 'concept' && <BookOpen size={24} />}
              {currentContent.type === 'example' && <Play size={24} />}
              {currentContent.type === 'practice' && <CheckCircle size={24} />}
              {currentContent.type === 'summary' && <CheckCircle size={24} />}
            </div>
            <div className="content-meta">
              <h2>{currentContent.title}</h2>
              <span className="duration">~{currentContent.duration} min read</span>
            </div>
          </div>

          <div className="content-body">
            {currentContent.content.split('\n').map((line, index) => {
              if (line.startsWith('**') && line.endsWith('**')) {
                return (
                  <strong key={index}>
                    {line.slice(2, -2)}
                  </strong>
                );
              }
              if (line.startsWith('•')) {
                return (
                  <li key={index} className="bullet-point">
                    {line.slice(1).trim()}
                  </li>
                );
              }
              if (line.trim() === '') {
                return <br key={index} />;
              }
              return <p key={index}>{line}</p>;
            })}
          </div>
        </div>

        <div className="navigation-buttons">
          <button
            onClick={handlePrevious}
            disabled={currentStep === 0}
            className="btn btn-secondary"
          >
            Previous
          </button>

          {currentStep < content.length - 1 ? (
            <button onClick={handleNext} className="btn btn-primary">
              Next
            </button>
          ) : (
            <button
              onClick={handleComplete}
              className="btn btn-primary"
            >
              {isCompleted ? 'Take Quiz' : 'Complete Learning'}
            </button>
          )}
        </div>
      </div>

      {isCompleted && (
        <div className="completion-banner">
          <CheckCircle size={24} />
          <span>Learning completed! You can now take the quiz to test your knowledge.</span>
        </div>
      )}
    </div>
  );
};

export default SectionLearn;
