import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { 
  Home, 
  BookOpen, 
  Trophy, 
  Shield, 
  User,
  LogOut,
  ChevronLeft,
  ChevronRight
} from 'lucide-react';
import './Sidebar.css';

function Sidebar() {
  const { logout } = useAuth();
  const location = useLocation();
  const [isCollapsed, setIsCollapsed] = useState(false);

  const navItems = [
    { path: '/', icon: Home, label: 'Home', category: 'LEARNING' },
    { path: '/modules', icon: BookOpen, label: 'Learn', category: 'LEARNING' },
    { path: '/leaderboard', icon: Trophy, label: 'Leaderboards', category: 'LEARNING' },
    { path: '/profile', icon: Shield, label: 'Data Breaches', category: 'ACCOUNT' },
    { path: '/profile', icon: User, label: 'Profile', category: 'ACCOUNT' },
  ];

  const groupedItems = navItems.reduce((acc, item) => {
    if (!acc[item.category]) {
      acc[item.category] = [];
    }
    acc[item.category].push(item);
    return acc;
  }, {});

  const handleLogout = () => {
    logout();
  };

  const toggleSidebar = () => {
    setIsCollapsed(!isCollapsed);
  };

  return (
    <div className={`sidebar ${isCollapsed ? 'collapsed' : ''}`}>
      <div className="sidebar-content">
        <div className="sidebar-toggle" onClick={toggleSidebar}>
          {isCollapsed ? <ChevronRight className="toggle-icon" /> : <ChevronLeft className="toggle-icon" />}
        </div>
        
        {Object.entries(groupedItems).map(([category, items]) => (
          <div key={category} className="nav-section">
            {!isCollapsed && <h3 className="nav-category">{category}</h3>}
            <nav className="nav-list">
              {items.map((item) => {
                const Icon = item.icon;
                const isActive = location.pathname === item.path;
                
                return (
                  <Link
                    key={item.path}
                    to={item.path}
                    className={`nav-item ${isActive ? 'active' : ''}`}
                    title={isCollapsed ? item.label : ''}
                  >
                    <Icon className="nav-icon" />
                    {!isCollapsed && <span className="nav-label">{item.label}</span>}
                  </Link>
                );
              })}
            </nav>
          </div>
        ))}
        
        <div className="nav-section">
          <button 
            onClick={handleLogout} 
            className="nav-item logout-btn"
            title={isCollapsed ? 'Logout' : ''}
          >
            <LogOut className="nav-icon" />
            {!isCollapsed && <span className="nav-label">Logout</span>}
          </button>
        </div>
      </div>
    </div>
  );
}

export default Sidebar;
