import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useSearchParams } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import { ArrowLeft, Save, Plus, Edit, Trash2 } from 'lucide-react';
import './SectionEditor.css';

function SectionEditor() {
  const { id } = useParams();
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const isNew = id === 'new';
  const moduleId = searchParams.get('module_id');
  
  const [section, setSection] = useState({
    module_id: moduleId ? parseInt(moduleId) : '',
    name: '',
    display_name: '',
    description: '',
    order_index: 1
  });
  const [questions, setQuestions] = useState([]);
  const [content, setContent] = useState([]);
  const [loading, setLoading] = useState(!isNew);
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    if (!user || !user.is_admin) {
      navigate('/admin');
      return;
    }
    if (!isNew) {
      loadSection();
    }
  }, [id, user, navigate, isNew]);

  const loadSection = async () => {
    try {
      const [sectionRes, questionsRes, contentRes] = await Promise.all([
        axios.get(`/api/admin/sections/${id}`),
        axios.get(`/api/admin/sections/${id}/questions`),
        axios.get(`/api/admin/sections/${id}/learning-content`)
      ]);
      setSection(sectionRes.data);
      setQuestions(questionsRes.data);
      setContent(contentRes.data);
    } catch (error) {
      console.error('Error loading section:', error);
      alert('Failed to load section');
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      if (isNew) {
        await axios.post('/api/admin/sections', section);
        navigate(`/admin/modules/${section.module_id}`);
      } else {
        await axios.put(`/api/admin/sections/${id}`, section);
      }
      alert('Section saved successfully!');
    } catch (error) {
      alert(error.response?.data?.error || 'Failed to save section');
    } finally {
      setSaving(false);
    }
  };

  const handleDeleteQuestion = async (questionId) => {
    if (!window.confirm('Are you sure you want to delete this question?')) {
      return;
    }
    try {
      await axios.delete(`/api/admin/questions/${questionId}`);
      loadSection();
    } catch (error) {
      alert(error.response?.data?.error || 'Failed to delete question');
    }
  };

  const handleDeleteContent = async (contentId) => {
    if (!window.confirm('Are you sure you want to delete this content?')) {
      return;
    }
    try {
      await axios.delete(`/api/admin/learning-content/${contentId}`);
      loadSection();
    } catch (error) {
      alert(error.response?.data?.error || 'Failed to delete content');
    }
  };

  if (loading) return <div className="admin-loading">Loading...</div>;

  return (
    <div className="section-editor">
      <div className="editor-header">
        <button onClick={() => navigate(`/admin/modules/${section.module_id || moduleId}`)} className="btn-back">
          <ArrowLeft /> Back
        </button>
        <h1>{isNew ? 'Create Section' : 'Edit Section'}</h1>
      </div>

      <div className="editor-form">
        <div className="form-group">
          <label>Module ID</label>
          <input
            type="number"
            value={section.module_id}
            onChange={(e) => setSection({ ...section, module_id: parseInt(e.target.value) || '' })}
            disabled={!isNew}
          />
        </div>

        <div className="form-group">
          <label>Name (internal)</label>
          <input
            type="text"
            value={section.name}
            onChange={(e) => setSection({ ...section, name: e.target.value })}
            placeholder="e.g., section-1-phishing"
          />
        </div>

        <div className="form-group">
          <label>Display Name</label>
          <input
            type="text"
            value={section.display_name}
            onChange={(e) => setSection({ ...section, display_name: e.target.value })}
            placeholder="e.g., Phishing and Social Engineering"
          />
        </div>

        <div className="form-group">
          <label>Description</label>
          <textarea
            value={section.description || ''}
            onChange={(e) => setSection({ ...section, description: e.target.value })}
            rows="3"
            placeholder="Section description..."
          />
        </div>

        <div className="form-group">
          <label>Order Index</label>
          <input
            type="number"
            value={section.order_index}
            onChange={(e) => setSection({ ...section, order_index: parseInt(e.target.value) || 1 })}
          />
        </div>

        <button onClick={handleSave} disabled={saving} className="btn-save">
          <Save /> {saving ? 'Saving...' : 'Save Section'}
        </button>
      </div>

      {!isNew && (
        <>
          <div className="questions-section">
            <div className="section-header">
              <h2>Questions</h2>
              <button onClick={() => navigate(`/admin/questions/new?section_id=${id}`)} className="btn-primary">
                <Plus /> Add Question
              </button>
            </div>
            <div className="items-list">
              {questions.map(question => (
                <div key={question.id} className="item-card">
                  <div className="item-info">
                    <h3>{question.question_text}</h3>
                    <p>Type: {question.question_type}</p>
                    <p>Options: {Array.isArray(question.options) ? question.options.length : 0}</p>
                  </div>
                  <div className="item-actions">
                    <button onClick={() => navigate(`/admin/questions/${question.id}`)} className="btn-edit">
                      <Edit /> Edit
                    </button>
                    <button onClick={() => handleDeleteQuestion(question.id)} className="btn-delete">
                      <Trash2 /> Delete
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="content-section">
            <div className="section-header">
              <h2>Learning Content</h2>
              <button onClick={() => navigate(`/admin/content/new?section_id=${id}`)} className="btn-primary">
                <Plus /> Add Content
              </button>
            </div>
            <div className="items-list">
              {content.map(item => (
                <div key={item.id} className="item-card">
                  <div className="item-info">
                    <h3>{item.screen_title}</h3>
                    <p>Read time: {item.read_time_min} min</p>
                    <p>Order: {item.order_index}</p>
                  </div>
                  <div className="item-actions">
                    <button onClick={() => navigate(`/admin/content/${item.id}`)} className="btn-edit">
                      <Edit /> Edit
                    </button>
                    <button onClick={() => handleDeleteContent(item.id)} className="btn-delete">
                      <Trash2 /> Delete
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default SectionEditor;
